import styled from 'styled-components';
import { shade } from 'polished';
// Template literals
export const Title = styled.h1`
  font-size: 48px;
  color: #3a3a3a;
  max-width: 450px;
  line-height: 56px;
  margin-top: 80px;
`;
export const Form = styled.form`
  margin-top: 40px;
  max-width: 700px;
  display: flex;
  input {
    flex: 1;
    height: 70px;
    border: 0;
    border-radius: 5px 0 0 5px;

    &::placeholder{
      color:: #a8a8 ;
    }
  }
  button {
    height: 70px;
    width: 210px;
    background: #04d361;
    border-radius: 0px 5px 5px 0px;
    border: 0;
    color: #fff;
    font-weight: bold;
    transition: background-color 0.2ss;

    &:hover {
      background: ${shade(0.2, '#04d361')};
    }
  }
`;
